    51→// AUTOMATICALLY GENERATED SERVICE
    52→import { APP_IDS } from '@/types/app';
    53→import type { UrlAnalyse } from '@/types/app';
    54→
    55→// Base Configuration
    56→const API_BASE_URL = 'https://my.living-apps.de/rest';
    57→
    58→// --- HELPER FUNCTIONS ---
    59→export function extractRecordId(url: string | null | undefined): string | null {
    60→  if (!url) return null;
    61→  // Extrahiere die letzten 24 Hex-Zeichen mit Regex
    62→  const match = url.match(/([a-f0-9]{24})$/i);
    63→  return match ? match[1] : null;
    64→}
    65→
    66→export function createRecordUrl(appId: string, recordId: string): string {
    67→  return `https://my.living-apps.de/rest/apps/${appId}/records/${recordId}`;
    68→}
    69→
    70→async function callApi(method: string, endpoint: string, data?: any) {
    71→  const response = await fetch(`${API_BASE_URL}${endpoint}`, {
    72→    method,
    73→    headers: { 'Content-Type': 'application/json' },
    74→    credentials: 'include',  // Nutze Session Cookies für Auth
    75→    body: data ? JSON.stringify(data) : undefined
    76→  });
    77→  if (!response.ok) throw new Error(await response.text());
    78→  // DELETE returns often empty body or simple status
    79→  if (method === 'DELETE') return true;
    80→  return response.json();
    81→}
    82→
    83→/** Upload a file to LivingApps. Returns the file URL for use in record fields. */
    84→export async function uploadFile(file: File | Blob, filename?: string): Promise<string> {
    85→  const formData = new FormData();
    86→  formData.append('file', file, filename ?? (file instanceof File ? file.name : 'upload'));
    87→  const res = await fetch(`${API_BASE_URL}/files`, {
    88→    method: 'POST',
    89→    credentials: 'include',
    90→    body: formData,
    91→  });
    92→  if (!res.ok) throw new Error(`File upload failed: ${res.status}`);
    93→  const data = await res.json();
    94→  return data.url;
    95→}
    96→
    97→export class LivingAppsService {
    98→  // --- URL_ANALYSE ---
    99→  static async getUrlAnalyse(): Promise<UrlAnalyse[]> {
   100→    const data = await callApi('GET', `/apps/${APP_IDS.URL_ANALYSE}/records`);
   101→    return Object.entries(data).map(([id, rec]: [string, any]) => ({
   102→      record_id: id, ...rec
   103→    }));
   104→  }
   105→  static async getUrlAnalyseEntry(id: string): Promise<UrlAnalyse | undefined> {
   106→    const data = await callApi('GET', `/apps/${APP_IDS.URL_ANALYSE}/records/${id}`);
   107→    return { record_id: data.id, ...data };
   108→  }
   109→  static async createUrlAnalyseEntry(fields: UrlAnalyse['fields']) {
   110→    return callApi('POST', `/apps/${APP_IDS.URL_ANALYSE}/records`, { fields });
   111→  }
   112→  static async updateUrlAnalyseEntry(id: string, fields: Partial<UrlAnalyse['fields']>) {
   113→    return callApi('PATCH', `/apps/${APP_IDS.URL_ANALYSE}/records/${id}`, { fields });
   114→  }
   115→  static async deleteUrlAnalyseEntry(id: string) {
   116→    return callApi('DELETE', `/apps/${APP_IDS.URL_ANALYSE}/records/${id}`);
   117→  }
   118→
   119→}
