   244→import { useState, useEffect, useMemo, useCallback } from 'react';
   245→import type { UrlAnalyse } from '@/types/app';
   246→import { LivingAppsService } from '@/services/livingAppsService';
   247→
   248→export function useDashboardData() {
   249→  const [urlAnalyse, setUrlAnalyse] = useState<UrlAnalyse[]>([]);
   250→  const [loading, setLoading] = useState(true);
   251→  const [error, setError] = useState<Error | null>(null);
   252→
   253→  const fetchAll = useCallback(async () => {
   254→    setError(null);
   255→    try {
   256→      const [urlAnalyseData] = await Promise.all([
   257→        LivingAppsService.getUrlAnalyse(),
   258→      ]);
   259→      setUrlAnalyse(urlAnalyseData);
   260→    } catch (err) {
   261→      setError(err instanceof Error ? err : new Error('Failed to load data'));
   262→    } finally {
   263→      setLoading(false);
   264→    }
   265→  }, []);
   266→
   267→  useEffect(() => { fetchAll(); }, [fetchAll]);
   268→
   269→  return { urlAnalyse, setUrlAnalyse, loading, error, fetchAll };
   270→}
