   316→## src/lib/ai.ts
   317→```
   318→const AI_ENDPOINT = "https://my.living-apps.de/litellm/v1/chat/completions";
   319→const AI_MODEL = "default";
   320→
   321→export type ChatMessage = {
   322→  role: "system" | "user" | "assistant";
   323→  content: string | Array<{ type: string; [key: string]: unknown }>;
   324→};
   325→
   326→type CompletionOptions = {
   327→  temperature?: number;
   328→  max_tokens?: number;
   329→  top_p?: number;
   330→  stop?: string[];
   331→  response_format?: { type: string };
   332→};
   333→
   334→export async function chatCompletion(
   335→  messages: ChatMessage[],
   336→  options: CompletionOptions = {}
   337→): Promise<string> {
   338→  const res = await fetch(AI_ENDPOINT, {
   339→    method: "POST",
   340→    headers: { "Content-Type": "application/json", Accept: "application/json" },
   341→    body: JSON.stringify({ model: AI_MODEL, messages, ...options }),
   342→  });
   343→  if (!res.ok) {
   344→    const body = await res.text();
   345→    throw new Error(`AI API ${res.status}: ${body}`);
   346→  }
   347→  const data = await res.json();
   348→  return data.choices[0].message.content;
   349→}
   350→
   351→export async function safeJsonCompletion<T = unknown>(
   352→  messages: ChatMessage[],
   353→  options: CompletionOptions = {}
   354→): Promise<T> {
   355→  const raw = await chatCompletion(messages, options);
   356→  const match = raw.match(/\{[\s\S]*\}|\[[\s\S]*\]/);
   357→  if (!match) throw new Error(`Expected JSON but got: ${raw.slice(0, 200)}`);
   358→  return JSON.parse(match[0]);
   359→}
   360→
   361→export async function withRetry<T>(fn: () => Promise<T>, maxAttempts = 3): Promise<T> {
   362→  for (let attempt = 1; attempt <= maxAttempts; attempt++) {
   363→    try {
   364→      return await fn();
   365→    } catch (err) {
   366→      if (attempt === maxAttempts) throw err;
   367→      await new Promise((r) => setTimeout(r, 1000 * attempt));
   368→    }
   369→  }
   370→  throw new Error("unreachable");
   371→}
   372→
   373→// --- File encoding helpers ---
   374→
   375→export function fileToDataUri(file: File): Promise<string> {
   376→  return new Promise((resolve, reject) => {
   377→    const reader = new FileReader();
   378→    reader.onload = () => resolve(reader.result as string);
   379→    reader.onerror = () => reject(new Error("Failed to read file"));
   380→    reader.readAsDataURL(file);
   381→  });
   382→}
   383→
   384→export async function urlToDataUri(url: string): Promise<string> {
   385→  const res = await fetch(url);
   386→  const blob = await res.blob();
   387→  return new Promise((resolve, reject) => {
   388→    const reader = new FileReader();
   389→    reader.onload = () => resolve(reader.result as string);
   390→    reader.onerror = () => reject(new Error("Failed to encode file"));
   391→    reader.readAsDataURL(blob);
   392→  });
   393→}
   394→
   395→/** Convert a data URI back to a Blob (for file uploads) */
   396→export function dataUriToBlob(dataUri: string): Blob {
   397→  const [header, base64] = dataUri.split(',');
   398→  const mime = header.match(/:(.*?);/)?.[1] ?? 'application/octet-stream';
   399→  const bytes = atob(base64);
   400→  const arr = new Uint8Array(bytes.length);
   401→  for (let i = 0; i < bytes.length; i++) arr[i] = bytes.charCodeAt(i);
   402→  return new Blob([arr], { type: mime });
   403→}
   404→
   405→// --- High-level AI features ---
   406→
   407→export async function classify(
   408→  text: string,
   409→  categories: string[]
   410→): Promise<{ category: string; confidence: number }> {
   411→  return safeJsonCompletion([
   412→    {
   413→      role: "system",
   414→      content: [
   415→        "You are a classifier. Respond ONLY with valid JSON, nothing else.",
   416→        'Output format: {"category": "<one of the allowed categories>", "confidence": <0-1>}',
   417→        `Allowed categories: ${JSON.stringify(categories)}`,
   418→      ].join("\n"),
   419→    },
   420→    { role: "user", content: text },
   421→  ], { temperature: 0 });
   422→}
   423→
   424→export async function extract<T = Record<string, unknown>>(
   425→  text: string,
   426→  schemaDescription: string
   427→): Promise<T> {
   428→  return safeJsonCompletion([
   429→    {
   430→      role: "system",
   431→      content: [
   432→        "You are a data extraction engine. Respond ONLY with valid JSON matching the requested schema.",
   433→        "If a field cannot be determined from the input, use null.",
   434→        `Schema:\n${schemaDescription}`,
   435→      ].join("\n"),
   436→    },
   437→    { role: "user", content: text },
   438→  ], { temperature: 0 });
   439→}
   440→
   441→export async function summarize(
   442→  text: string,
   443→  options: { maxSentences?: number; language?: string } = {}
   444→): Promise<string> {
   445→  const { maxSentences = 3, language } = options;
   446→  const instructions = [
   447→    `Summarize the following text in at most ${maxSentences} sentences.`,
   448→    "Be concise and preserve key facts.",
   449→  ];
   450→  if (language) instructions.push(`Write the summary in ${language}.`);
   451→  return chatCompletion([
   452→    { role: "system", content: instructions.join(" ") },
   453→    { role: "user", content: text },
   454→  ]);
   455→}
   456→
   457→export async function translate(
   458→  text: string,
   459→  targetLanguage: string,
   460→  sourceLanguage?: string
   461→): Promise<string> {
   462→  const from = sourceLanguage ? ` from ${sourceLanguage}` : "";
   463→  return chatCompletion([
   464→    {
   465→      role: "system",
   466→      content: `Translate the following text${from} to ${targetLanguage}. Output ONLY the translation, nothing else.`,
   467→    },
   468→    { role: "user", content: text },
   469→  ]);
   470→}
   471→
   472→export async function analyzeImage(imageDataUri: string, prompt: string): Promise<string> {
   473→  return chatCompletion([
   474→    {
   475→      role: "user",
   476→      content: [
   477→        { type: "text", text: prompt },
   478→        { type: "image_url", image_url: { url: imageDataUri } },
   479→      ],
   480→    },
   481→  ]);
   482→}
   483→
   484→export async function analyzeDocument(fileDataUri: string, prompt: string): Promise<string> {
   485→  return chatCompletion([
   486→    {
   487→      role: "user",
   488→      content: [
   489→        { type: "text", text: prompt },
   490→        { type: "file", file: { file_data: fileDataUri } },
   491→      ],
   492→    },
   493→  ]);
   494→}
   495→
   496→export async function extractFromPhoto<T = Record<string, unknown>>(
   497→  dataUri: string,
   498→  schemaDescription: string
   499→): Promise<T> {
   500→  const isImage = dataUri.startsWith("data:image/");
   501→  const fileContent = isImage
   502→    ? { type: "image_url" as const, image_url: { url: dataUri } }
   503→    : { type: "file" as const, file: { file_data: dataUri } };
   504→  return safeJsonCompletion([
   505→    {
   506→      role: "system",
   507→      content: [
   508→        "Extract structured data from the provided " + (isImage ? "image" : "document") + ".",
   509→        "Respond ONLY with valid JSON matching the schema.",
   510→        "Use null for any field that cannot be determined.",
   511→        `Schema:\n${schemaDescription}`,
   512→      ].join("\n"),
   513→    },
   514→    {
   515→      role: "user",
   516→      content: [
   517→        { type: "text", text: "Extract the data from this " + (isImage ? "image" : "document") + "." },
   518→        fileContent,
   519→      ],
   520→    },
   521→  ]);
   522→}
   523→
   524→```
